sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // rightnow stores the current time
    var rightnow = new GlideDateTime();

    // Create a GlideDateTime object for the When needed date
    var whenNeeded = new GlideDateTime(current.u_when_needed);

    // Check if the "When needed" date is in the past
    if (whenNeeded.before(rightnow)) {
        gs.addErrorMessage("When needed date cannot be in the past. Your request has not been saved to the database.");
        current.setAbortAction(true);
        return;
    }

    // Create a GlideDateTime object for the Requested Date
    var requestedDate = new GlideDateTime(current.u_requested_for);

    // Set the time portion of the current date to midnight (00:00:00).
    rightnow.setDisplayValue(rightnow.getDate().getLocalDate() + " 00:00:00");

    // Check if the "requested_for" date is the same as the current date
    if (requestedDate.equals(rightnow)) {
        gs.addErrorMessage("Same-day requests are not allowed. Please choose a future date.");
        current.setAbortAction(true);
    }
    // Check if the "requested_for" date is in the future
    else if (requestedDate.after(rightnow)) {
        gs.addErrorMessage("Requested date cannot be in the past. Your request has not been saved to the database.");
        current.setAbortAction(true);
    }
})(current, previous);
