sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	var grInc = new GlideRecord('incident');
  grInc.addEncodedQuery('caller_id=' + gs.getUserID());
  grInc.query();


  if (grInc.next()) {

    data.number = grInc.number.toString();
    data.short_description = grInc.short_description.toString();
    data.caller_id = grInc.getDisplayValue('caller_id');
  }
})();
